// 1. Import multer.
import multer from "multer";

// 2. Configure storage with filename and location.

const storage = multer.diskStorage({
  destination: (req, file, cb) => {
    cb(null, "./uploads/");
  },
  filename: (req, file, cb) => {
    // Invalid Filename Characters: The filename generated by new Date().toISOString() contains colons (:), which are not allowed in Windows file paths. When you concatenate this with the original filename, it creates an invalid path.
    const timestamp = new Date().toISOString().replace(/:/g, "-"); // Replace colons with hyphens
    cb(null, `${timestamp}${file.originalname}`);
  },
});

export const upload = multer({
  storage: storage,
});
